mapper.update:
UPDATE t_user
nick_name=#{nickName}, head_url=#{headUrl}, password=#{password}, phone=#{phone}, we_chat_open_id=#{weChatId}, qq_open_id=#{qqId}, signature=#{signature}, login_time=#{loginTime}, modify_time=NOW()
where id = #{id} or user_name = #{id} or phone = #{id}or we_chat_open_id = #{id}or qq_open_id = #{id};
